class Kaitai(Directive):
    required_arguments = 1
    final_argument_whitespace = True
    has_content = False

    def data(self, data):
        if isinstance(data, list):
            node = nodes.enumerated_list()
            for v in data:
                list_item = nodes.list_item()
                list_item += self.data(v)
                node += list_item
            return node
        if isinstance(data, dict):
            node = nodes.field_list(classes=["kaitai"])
            for id, val in data.items():
                field = nodes.field()
                field += nodes.field_name(text=str(id))
                var_body = nodes.field_body()
                var_body += self.data(val)
                field += var_body
                node += field
            return node
        if isinstance(data, str) or isinstance(data, int) or isinstance(data, float):
            return nodes.literal(text=str(data))
        else:
            return nodes.literal(text=f"{type(data)} {data}")
    
    def seq(self, data, id):
        section = nodes.section(ids=f"{id}-seq")
        section += nodes.title(text="Sequence")
        section += self.data(data)
        return [section]

    def enums(self, data, id):
        enums_section = nodes.section(ids=[f"{id}-enums"])
        enums_section += nodes.title(text = "Enums")
        for key, value in data.items():
            enum_section = nodes.section(ids=[f"kaitai-enums-{key}"])
            enum_title = nodes.title()
            enum_title += nodes.generated(text="Enum ")
            enum_title += nodes.literal(text=key)
            enum_section += enum_title
            list = nodes.field_list()
            for id, val in value.items():
                field = nodes.field()
                field += nodes.field_name(text=str(id))
                var_body = nodes.field_body()
                var_body += nodes.literal(text=str(val))
                field += var_body
                list += field
            enum_section += list
            enums_section += enum_section
        return enums_section
    
    def types(self, data, id):
        id = f"{id}-types"
        section = nodes.section(ids=[id])
        section += nodes.title(text = "Types")
        for key, value in data.items():
            type_id = f"{id}-{key}"
            type_section = nodes.section(ids=[type_id])
            type_title = nodes.title()
            type_title += nodes.generated(text="Type ")
            type_title += nodes.literal(text=key)
            type_section += type_title
            for id, val in value.items():
                if id == 'seq':
                    type_section.extend(self.seq(val, type_id))
                else:
                    sec = nodes.section(ids = [f"{type_id}-{id}"])
                    sec += nodes.title(text = id)
                    sec += self.data(val)
                    type_section += sec
            section += type_section
        return section

    def run(self):
        try:
            file = directives.path(self.arguments[0])
            doc = self.state_machine.document
            src = doc.current_source
            dir = os.path.dirname(src)
            path = os.path.normpath(os.path.join(dir, file))
            
            with open(path, 'r') as ksy_file:
                data = yaml.safe_load(ksy_file)
                id = data['meta']['id']
                section = nodes.section(ids=[id])
                section += nodes.title(text=f"Binary Format")
                for key, value in data.items():
                    if key == 'enums':
                        section += self.enums(value, id)
                    elif key == 'types':
                        section += self.types(value, id)
                    elif key == 'seq':
                        section += self.seq(value, id)
                    else:
                        sec = nodes.section(ids = [f"{id}-{key}"])
                        sec += nodes.title(text = key)
                        sec += self.data(value)
                        section += sec
                return [section]
        except Exception as e:
            return [nodes.paragraph(text=str(e))]

def wiki_role(role, rawtext, text, lineno, inliner, options={}, content=[]):
    ref = wiki_base_url + text
    set_classes(options)
    title = utils.unescape(text)
    node = nodes.reference(rawtext, title, refuri=ref, **options)
    return [node], []

def lu_packet_role(role, rawtext, text, lineno, inliner, options={}, content=[]):
    ref = lu_packet_base_url + '%s.html' % text
    set_classes(options)
    title = utils.unescape(text)
    node = nodes.reference(rawtext, title, refuri=ref, **options)
    return [node], []

def lu_gm_role(key):
    def role(role, rawtext, text, lineno, inliner, options={}, content=[]):
        ref = lu_packet_base_url + f'world/gm{key}/struct.{text}.html'
        set_classes(options)
        title = utils.unescape(text)
        node = nodes.reference(rawtext, title, refuri=ref, **options)
        return [node], []
    return role

def script_role(role, rawtext, text, lineno, inliner, options={}, content=[]):
    ref = explorer_base_url + 'scripts/' + text
    set_classes(options)
    title = utils.unescape(text)
    lit = nodes.literal(rawtext, title)
    node = nodes.reference('', 'Script ', lit, refuri=ref, **options)
    return [node], []

obj_match = re.compile(r"(.*) <([0-9]+)>")

def explorer_role(prefix, part_url):
    def role(role, rawtext, text, lineno, inliner,
                 options={}, content=[]):
        m = obj_match.match(text)
        if m:
            num = int(m.group(2))
            title = utils.unescape(m.group(1))
        else:
            try:
                num = int(text)
                title = prefix + utils.unescape(text)
                if num <= 0:
                    raise ValueError
            except ValueError:
                msg = inliner.reporter.error(
                    prefix + 'number must be a number greater than or equal to 1; '
                    '"%s" is invalid.' % text, line=lineno)
                prb = inliner.problematic(rawtext, rawtext, msg)
                return [prb], [msg]

        ref = explorer_base_url + part_url % num
        set_classes(options)
        node = nodes.reference(rawtext, title, refuri=ref, **options)
        return [node], []
    return role

behavior_role = explorer_role('Behavior ', 'skills/behaviors/%d')
skill_role = explorer_role('Skill ', 'skills/%d')
activity_role = explorer_role('Activity ', 'activities/%d')
object_role = explorer_role('Object ', 'objects/%d')
mission_role = explorer_role('Mission ', 'missions/%d')
zone_role = explorer_role('Zone ', 'zones/%d')

class GMDomain(Domain):
    label = 'Game Message'
    name = 'gm'

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

def setup(app):
    app.add_directive('kaitai', Kaitai)
    app.add_role('behavior', behavior_role)
    app.add_role('skill', skill_role)
    app.add_role('mis', mission_role)
    app.add_role('lot', object_role)
    app.add_role('zone', zone_role)
    app.add_role('script', script_role)
    app.add_role('wiki', wiki_role)
    app.add_domain(GMDomain)
    app.add_role('gm', lu_gm_role(''))
    app.add_role_to_domain('gm', 'server', lu_gm_role('/server'))
    app.add_role_to_domain('gm', 'client', lu_gm_role('/client'))
    app.add_role('packet', lu_packet_role)
    app.add_css_file("css/packets.css")
    app.add_role('act', activity_role)

